',      id               display_seq,',
'master*10000000+(id_padre*100000)+id orden',
'  FROM PARAMETROS.PA_ROLES_OPCIONES A, PARAMETROS.PA_ROLES_USUARIO B , PARAMETROS.PA_OPCIONES C, PARAMETROS.PA_USUARIOS D  ',
'  WHERE C.COD_CIA = A.COD_CIA  ',
'    AND C.ID_MENUOP = A.ID_MENUOP',
'    AND C.MODULO = A.MODULO',
'    AND B.COD_CIA = A.COD_CIA',
